css:
@-moz-document url-prefix("http://randomc.net/") {
.post p,
.post ul li {
    color: #444;
    font-size: 14px;
}
.post .the_content p {
    line-height: 1.5;
    margin-bottom: 10px;
}
.post .the_content ul li {
    line-height: 1.4;
}
.post .commenttext p:not(.postmeta) {
    line-height: 1.5;
}
}
