css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

  /* == Gmail Mail List Color == */
  
  /* Unread Line */
  .zE {
    color: #222;
/*     background: rgba(247, 246, 242, 0.9) none repeat scroll 0% 0%; */
    background: rgba(255, 250, 220, 0.8) none repeat scroll 0% 0%;
  }
  /* Readed Line */
  .yO {
    color: #222;
    background: rgba(245, 247, 247, 0.8) none repeat scroll 0% 0%;
  }
  
  /* == account name == */
  a.gb_R:before {
    content: attr(title);
    position: absolute;
    left: -300px;
    top: 30px;
    font-size: 120%;
    font-weight: normal;
    font-stretch: condensed;
    color: rgba(200, 200, 200, 1);
    mix-blend-mode: overlay;
  }
  a.gb_R:after {
    content: attr(title);
    position: absolute;
    left: -299px;
    top: 31px;
    font-size: 120%;
    font-weight: normal;
    font-stretch: condensed;
    color: rgba(50, 50, 50, 1);
    mix-blend-mode: overlay;
  }  
}
