css:
@-moz-document domain("joueurdugrenier.fr"), domain("joueurdugrenier.spreadshirt.fr") {
body {
    background-image: radial-gradient(circle, #f9a72b 0%, #fa9026 70%, #fb6c1f 100%)!important;
}
#wpadminbar {
    color: #fff!important;
    background: #222!important;
}
#top-bar, #header-menu-wrap, .sidebar h3 {
    background: none!important;
    background-color: #222!important;
}
.avatar_container img {
    padding: 1px!important;
    border: 1px solid #404040!important;
    background: #404040;
}
img[src='http://www.joueurdugrenier.fr/wp-content/themes/graphene/images/social/rss.png']{
    content: url(http://image.noelshack.com/fichiers/2016/09/1456832557-lqbguqe.png)!important;
    width:30px!important;
    -webkit-filter: invert(25%); /* Chrome, Safari, Opera */
    filter: invert(25%);
}
img[src='http://www.joueurdugrenier.fr/wp-content/themes/graphene/images/social/facebook.png']{
    content: url(http://image.noelshack.com/fichiers/2016/09/1456832603-pcjh7st.png)!important;
    width:30px!important;
	-webkit-filter: invert(25%); /* Chrome, Safari, Opera */
    filter: invert(25%);
}
img[src='http://www.joueurdugrenier.fr/wp-content/themes/graphene/images/social/twitter.png']{
    content: url(http://image.noelshack.com/fichiers/2016/09/1456832644-jbeh13a.png)!important;
    width:30px!important;
	-webkit-filter: invert(25%); /* Chrome, Safari, Opera */
    filter: invert(25%);
}
div#header{
    background-image: url(http://image.noelshack.com/fichiers/2016/09/1456832678-d05dbef.png)!important;
}
#logo_header{
    margin: 0 0 0 0!important;
    padding: 200px 0 0 690px!important;
    background:no-repeat top!important;
}
.sidebar h3 {
    text-shadow: none!important;
    border-color: #2387BE!important;
    border-bottom: 3px solid #2387BE!important;
}
.sidebar h3, .sidebar h3 a, .sidebar h3 a:visited {
    color: #F7B126!important;
}
#content {
    background: #FFFFFF!important;
}
#sidebar_indextop {
    background: none!important;
}
#header-menu > li > a {
    color: #F7B126;
}
#header-menu > li:hover, #header-menu > li.current-menu-item, #header-menu > li.current-menu-ancestor {
    background:  #F7B126;
}
#header-menu > li:hover > a, #header-menu > li.current-menu-item > a, #header-menu > li.current-menu-ancestor > a {
    color: #222;
}
#header-menu ul li {
    background: #F7B126;
}
#header-menu ul li:hover > a, #header-menu ul li.current-menu-item > a, #header-menu ul li.current-menu-ancestor > a {
    color: #F7B126;
}
.block-button, .block-button:visited, .Button, .button, input[type="submit"], button[type="submit"], #commentform #submit, .wpsc_buy_button {
    background: #F7B126!important;
    border-color: rgba(34, 34, 34, 0.35)!important;
    color: rgba(34, 34, 34, 0.75)!important;
    text-shadow: none!important;
}
.page-title {
    background: #222222!important;
    color: #F7B126!important;
}
#wpucssmagic .wpucssmagic .headerbar {
    background-color: #222222!important;
	background-image: none!important;
}
#wpucssmagic .wpucssmagic .forabg, #wpucssmagic .wpucssmagic .forumbg {
    background-color: #222222;
    background-image: none;
}
#wpucssmagic .wpucssmagic ul.forums {
    background-color: rgb(201, 201, 201);
    background-image: none;
}
#wpucssmagic .wpucssmagic .navbar {
    background-color: #C9C9C9;
}
#wpucssmagic .wpucssmagic #search-box #keywords {
    background-color: #222;
}
#wpucssmagic .wpucssmagic #search-box input {
    border-color: #F7B126;
    border-radius: 5px;
}
#wpucssmagic .wpucssmagic input.search {
    background-image: none;
}
#wpucssmagic .wpucssmagic .inputbox {
    color: #C9C9C9!important;
}
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #F7B126 ! important
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #222 ! important;
}
}
