css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soompi.com") {

  body {
    background : #ECEFF4 url("http://nsm08.casimages.com/img/2016/02/02//16020210543710339313943133.jpg") left fixed no-repeat !important ;
    background-size : auto 100% !important ;
  }
  
  body #realm-center, body .page-center {margin-right : 25px !important ;}
  
  .one-sidebar {
    background : black !important ;
    color : #ECEFF4 !important ;
  }
  /*
  table, p, span, label, .default-txt, time, .entry-date, a, .default-link, .fn, input[type="submit"], li {color : #ECEFF4 !important ;}*/
  article.type-post p {color : #ECEFF4 !important ;}
  
  article.type-post a {color : #C39061 !important ;}
  
  
  .hr-gradient {background : grey !important ;}
  
  .single .content-item .tags a, .page .content-item .tags a, .home footer.section-footer .category-menu ul li a {background : lightgrey !important ; color : #BF5A40 !important ;}
  
  h1, .header-1, h1 a, h1 span, .header-1 a, .header-1 span {color : #BF5A40 !important ; text-shadow : 1px 1px 0 #E2AF9C !important ;}
  #home-features h1, #gallery-features h1 {color : white !important ; text-shadow : 1px 1px 0 lightgrey !important ;}
  
  .pagination a {color : #BF5A40 !important ;}
  .pagination .current, .pagination .current:hover {background : #BF5A40 !important ; color : white !important ; border-color : #E2AF9C !important ;}
  
  .single #nav-single .inner p, .page #nav-single .inner p {color : #BF5A40 !important ;}
  
  
  .single .content-item .content-header .content-meta .fn .author, .page .content-item .content-header .content-meta .fn .author {color : #c39061 !important ; border-bottom : 1px dotted #c39061 !important ;}
  
  /*Colonne latérale*/
  
  .widget.whats_hot .tab_content .trending_posts ul li.item_1, .widget.whats_hot .tab_content .recent_posts ul li.item_1,
  .widget.whats_hot .tab_content .trending_posts ul li.item_2, .widget.whats_hot .tab_content .recent_posts ul li.item_2,
  .widget.whats_hot .tab_content .trending_posts ul li.item_3, .widget.whats_hot .tab_content .recent_posts ul li.item_3,
  .widget.whats_hot .tab_content .trending_posts ul li.item_4, .widget.whats_hot .tab_content .recent_posts ul li.item_4,
  .widget.whats_hot .tab_content .trending_posts ul li.item_5, .widget.whats_hot .tab_content .recent_posts ul li.item_5
  {background : transparent !important ;}
 
  
  .content-list .content-item .fn .author {color : #BF5A40 !important ;}

}
