css:
@-moz-document domain("vedabase.com") {
  body {
      background: white;
  }

  ::selection {
      background: #F9D462;
  }

  #vb-toolbar-wrapper {
      border-bottom-color: #999;
  }

  .vb-language-switcher a {
      background: none;
  }

  .logo-img a img {
      display: none;
  }

  .logo-img a:before {
      content: 'Bhaktivedanta VedaBase';
  }

  .logo-img {
      font-family: 'Gaura Times', 'vbGauraTimes', Junicode, 'IndUni-T', 'FreeSerif', 'Times New Roman', 'DejaVu Serif', times, times-roman, georgia, serif;
      font-weight: normal;
      font-size: 36px;
      line-height: 1.3em;
  }

  .logo-img a {
      color: black;
      text-decoration: none;
  }

  .region-menu,
  .verse-pager {
      border-top: solid 1px #999;
      border-bottom: solid 1px #999;
      text-align: center;
      margin-bottom: 1em;
  }

  .region-menu,
  .region-menu .region-menu-inner,
  .verse-pager,
  .verse-pager ul.custom-pager {
      background: none;
  }

  .breadcrumb {
      text-align: center;
  }

  #vb-toolbar-clipboard span,
  #vb-toolbar-clipboard span.invisible {
      background-color: transparent;
      border: 1px solid #999;
  }
}
