css:
@-moz-document url(https://mightytext.net/web8/) {

/*========================Auto-Hide Left Side Bar========================*/
#sideNavigation{
	z-index:10 !important;
	-moz-transition-duration: .2s !important;
	-webkit-transition-duration: .2s !important;
	position:fixed !important;
	width: 200px !important;
	height: 100% !important;
	padding-left: 30px !important;
  padding-right: 10px !important;
	background-color: #111111 !important;
	opacity: 0 !important;
	left: -235px !important;
	border-right: #DD4B39 5px solid !important;
	margin-left: 30px !important;
}

#sideNavigation:hover {
	-webkit-transition-duration: .2s !important;
	-moz-transition-duration: .2s !important;
	background-color: #2F2F2F !important;
	position:fixed !important;
	z-index:10 !important;
	opacity: 1 !important;
	-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
		box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
		-webkit-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
	left: -30px !important;
}

#pinnedContent {left: 0px	

}}
