css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.fefe.de") {
  
  @font-face {
    font-family: 'Raleway';
    font-style: normal;
    font-weight: 400;
    src: local('Raleway'), url(http://fonts.gstatic.com/s/raleway/v9/IczWvq5y_Cwwv_rBjOtT0w.woff) format('woff');
  }
  * {font-family: Raleway !important;}
  ::-moz-selection { background: #252525; color: #e6db74; box-shadow: 0 0 5px rgba(0,0,0,1); }
  ::selection { background: #252525; color: #e6db74; box-shadow: 0 0 5px rgba(0,0,0,1); }
  body{ background: #2f2f2f url(http://btcdb.net/img/back-black.jpg) repeat-x center center fixed; background-size: cover; color: #fefefe !important; font-size: .9rem; line-height: 2rem; text-shadow: 1px 1px 2px rgba(0,0,0,.5); }
  h2 > a:link {display: none; }
  h2:after {content: "FEFEs BLOG"; color: #fff; font-weight: bold;}
  h3 { 
    color: #252525;
    text-shadow: 1px 1px 0 rgba(255,255,255,.25);
    font-weight: bold;
    text-align: center;
    box-shadow: 0 2px rgba(0,0,0,.25);
    background: -moz-linear-gradient(-45deg,  rgba(255,255,255,0) 0%, rgba(255,255,255,0.25) 50%, rgba(255,255,255,0) 100%); 
    background: linear-gradient(135deg,  rgba(255,255,255,0) 0%,rgba(255,255,255,0.25) 50%,rgba(255,255,255,0) 100%); 
  }
  a:link, a:hover, a:active{ color:#66d9ef; text-decoration: none; border-bottom: 1px dashed #66d9ef; }
  a:visited{ color:#f92772; text-decoration: none;  border-bottom: 1px dashed #f92772; }
  ul > li{ list-style: none; }
  ul > li > a:first-child { background: #2a2a2a; margin-right: 1rem; border-radius: 5px; color: #2a2a2a; box-shadow: 0 0 5px #000; }
  div[style="text-align:center"] { margin-top: 2.5rem; }
  div[style="text-align:center"] > a{ background:#2a2a2a; color: #aaa; font-weight: bold;  width: 10rem; padding: .5rem 1rem; border-radius: 3px; box-shadow: 2px 2px 3px rgba(0,0,0,.5); border-bottom: none; }
}
