css:
@-moz-document url-prefix('https://www.gcinema.net/logs')
{
  
body, div, span, b, a, i
{
 font-family: Roboto, MS PGothic, Segoe UI Symbol;
 font-size: 10pt;
 text-align: left;
 font-weight: normal;
 text-decoration: none;
 background: #ffffff;
 font-style: normal;
}

.ban, .blacklisted, .removed, .kick { color: #ff0000 !important; }
.ban a, .blacklisted a, .removed a, .kick a, .kick i { color: #990000 !important; }
.unban, .unblacklisted { color: #009900 !important; }
.unban a, .unblacklist a { color: #006600 !important; }

b a { color: #ff8800; }
b i a { color: #0000ff; }
span.at { color: #008800; font-style: normal; } 

.chat span b i
{
 font-family: Verdana;
 font-weight: bold;
 font-size: 12px;
}

.event
{
 display: block;
 background: #ffffff;
 width: 100%;
 text-align: left;
 margin: 8px !important;
 border-radius: 0px;
 height: 10px !important;
 box-shadow: none;
}

.body
{
 width: 100%;
 margin: 0px;
 padding: 10px;
}

.breadcrumb { visibility: hidden; }

}
