css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www\\.)?smogon\\.com/forums/.*") {

	/** COLOR **/
	body,
	.explain .CurrentStatus,
	.PanelScroller .navControls a,
	.searchResult .snippet a,
	.tabs li a,
	.tabs.noLinks li,
	.textHeading,
	.xenForm .sectionHeader
	{
		color: #d4d4d4;
	}
	
	body .muted,
	body .muted a,
	body a.muted
	{
		color: #a5a5a5;
	}
	
	a.caloff
	{
		color: #969696;
	}
	
	.PageNav a,
	.PageNav a:focus,
	.PageNav a:hover
	{
		color: #8f6c3f;
	}
	
	a:link,
	a:visited,
	.blockLinksList a,
	.blockLinksList label,
	#caltitle,
	#caldays span,
	.ugc a:focus,
	.ugc a:hover
	{
		color: #80bae1;
	}
	
	.larger.textHeading,
	.xenForm .sectionHeader
	{
		color: #6a6a8c;
	}
	
	
	/** BACKGROUND-COLOR **/
	.profilePage .tabs.mainTabs li a:hover,
	.tabs li:hover a,
	.tabs.noLinks li:hover
	{
		background-color: #6a6a8c;
	}
	
	.bbCodeQuote .quoteContainer
	{
		background-color: #555666;
	}
	
	.navPopup .listPlaceholder ol.secondaryContent.Unread
	{
		background-color: #525284;
	}
	
	.breadcrumb .crust:hover a.crumb,
	.PanelScroller .scrollContainer,
	.PanelScrollerOff .panel,
	.PanelScroller .panel,
	.PanelScrollerOff .panel,
	.PanelScroller .navControls a.current,
	.PanelScroller .navControls a.current:hover,
	.sectionFooter,
	.messageUserBlock,
	#QuickSearch.active,
	.active .formPopup,
	.formPopup .controlsWrapper,
	ul.autoCompleteList li.selected,
	ul.autoCompleteList li:hover,
	.blockLinksList a:focus,
	.blockLinksList a:hover,
	.blockLinksList label:focus,
	.blockLinksList label:hover,
	.blockLinksList li.kbSelect a,
	.blockLinksList li.kbSelect label,
	.navigationSideBar a:hover,
	.navPopup .PopupItemLinkActive:hover,
	.tabs li a,
	.tabs.noLinks li
	{
		background-color: #4f4f75;
	}
	
	.breadcrumb,
	.breadcrumb .crust a.crumb,
	.PanelScroller .navControls a,
	.PanelScroller .navControls a:hover,
	.node .nodeLastPost,
	.secondaryContent,
	.discussionListItem .posterAvatar,
	.discussionListItem .stats,
	.messageUserBlock div.avatarHolder,
	.messageUserBlock .extraUserInfo,
	ul.autoCompleteList,
	.calfocus,
	.calweek a:hover,
	.profilePage .tabs.mainTabs,
	.tabs,
	.bbCodeBlock .code,
	.bbCodeBlock pre
	{
		background-color: #4a4a5f;
	}
	
	#content .pageContent,
	.sectionMain,
	.nodeList .categoryForumNodeInfo,
	.nodeList .forumNodeInfo,
	.nodeList .linkNodeInfo,
	.nodeList .pageNodeInfo,
	.discussionListItem,
	.primaryContent,
	.Menu .primaryContent,
	.Menu .secondaryContent,
	.Menu .sectionFooter,
	#DiscussionListOptionsHandle a,
	#calroot,
	.profilePage .tabs.mainTabs li.active a,
	.avatar .img,
	.avatar img,
	.avatarCropper
	{
		background-color: #414155;
	}
	
	#QuickSearch,
	.formPopup,
	.ugc a:focus,
	.ugc a:hover
	{
		background-color: rgba(0,0,0,0);
	}
	
	
	/** BACKGROUND-IMAGE **/
	.breadcrumb,
	.PanelScroller .panel,
	.PanelScrollerOff .panel,
	.node .nodeLastPost,
	.secondaryContent,
	.sectionFooter,
	.discussionListItem .posterAvatar,
	.discussionListItem .stats,
	.formPopup .controlsWrapper,
	ul.autoCompleteList,
	.navigationSideBar a:hover,
	.tabs,
	.tabs li a, .tabs.noLinks li,
	.bbCodeQuote .quoteContainer,
	.bbCodeBlock .code,
	.bbCodeBlock pre,
	.ugc a:focus,
	.ugc a:hover
	{
		background-image: none;
	}
	
	
	/** BORDER-COLOR **/
	#calroot
	{
		border-color: #80bae1;
	}
	
	.breadcrumb,
	.PanelScroller .scrollContainer,
	.PanelScrollerOff .panel,
	.PanelScroller .navControls a,
	.sectionMain,
	.tabs li a,
	.tabs.noLinks li,
	.avatar .img,
	.avatar img,
	.avatarCropper
	{
		border-color: #6a6a8c;
	}
	
	.node .nodeLastPost,
	.message .likesSummary,
	.messageUserBlock,
	.profilePage .mast .section.infoBlock,
	.profilePage .mast .followBlocks .primaryContent.avatarHeap,
	.conversation_view .messageList
	{
		border-color: #4f4f75;
	}
	
	.Menu
	{
		border-color: #202072;
	}
	
	/** BORDER-TOP-COLOR **/
	.messageList .message,
	.xenForm .formGroup,
	.xenForm fieldset,
	.xenForm .formGroup + .ctrlUnit,
	.xenForm .submitUnit,
	.xenForm fieldset + .ctrlUnit,
	.profilePage .primaryUserBlock,
	.profilePage .primaryUserBlock .lastActivity,
	.thread_online .messageList
	{
		border-top-color: #4f4f75;
	}
	
	/** BORDER-LEFT-COLOR **/
	.breadcrumb .crust .arrow,
	#DiscussionListOptionsHandle a
	{
		border-left-color: #6a6a8c;
	}
	
	.breadcrumb .crust .arrow span
	{
		border-left-color: #4a4a5f;
	}
	
	.breadcrumb .crust:hover .arrow span,
	.discussionListItem .stats dl,
	.profilePage .mast .sectionFooter,
	.profilePage .mainProfileColumn
	{
		border-left-color: #4f4f75;
	}
	
	/** BORDER-RIGHT-COLOR **/
	#DiscussionListOptionsHandle a
	{
		border-right-color: #6a6a8c;
	}
	
	.discussionListItem .stats dl,
	.profilePage .mast .sectionFooter,
	.profilePage .mast
	{
		border-right-color: #4f4f75;
	}
	
	/** BORDER-BOTTOM-COLOR **/
	#caldays
	{
		border-bottom-color: #80bae1;
	}
	
	.sectionFooter,
	#DiscussionListOptionsHandle a,
	.profilePage .mast .sectionFooter,
	.tabs
	{
		border-bottom-color: #6a6a8c;
	}
	
	.nodeList .categoryForumNodeInfo,
	.nodeList .forumNodeInfo,
	.nodeList .linkNodeInfo,
	.nodeList .pageNodeInfo,
	.secondaryContent,
	.profilePage .mast .section.infoBlock h3,
	.sidebar .section .primaryContent h3,
	.sidebar .section .secondaryContent h3,
	.discussionListItem,
	.primaryContent,
	.textHeading,
	.xenForm .sectionHeader,
	.navPopup .listItem
	{
		border-bottom-color: #4f4f75;
	}
	
	/** OTHER **/
	.emCtrl:focus,
	.emCtrl:hover,
	.ugc a:focus,
	.ugc a:hover
	{
		box-shadow: none;
	}
	
	.emCtrl:focus,
	.emCtrl:hover,
	.ugc a:focus,
	.ugc a:hover
	{
		text-decoration: underline;
	}

}
