css:
@-moz-document domain("wikisource.org") {
#mw-content-text, #footertemplate {
    font-family: Georgia;
    font-size: small;
    width: 600px;
    margin: auto;
    margin-right: 400px;
    padding: 10px;
    line-height: 150%;
    background-color: #3BB176;
    border: 1px solid #47C284;
}
.gen_header_title, .searchaux, #toc, #catlinks, .table-row, .thumbcaption, .authortemplate, .author_notes, tbody, #plainSister, .thumbinner, .processheadertemplate, .process_notes {
    background-color: #3BB176 !important;
    border: none !important;
}
#content, body, #mw-head, #mw-navigation {
    background-color: DimGray;
}
.mw-body {
    border: none;
}
#footer, #mw-panel, #left-navigation, #p-views, #p-personal, #contentSub, #p-cactions, #mw-head-base, #mw-page-base, .firstHeading, .licenseContainer.licenseBanner, .navbox.acContainer {
    display: none;
}
a {
    color: #06130d !important;
    text-decoration: none!important;
}
a:hover {
    text-decoration: underline !important;
}
a:visited {
    color: #1a4c33 !important;
}
}
