css:
@-moz-document regexp(".*rabbitmq.*") {
* {
    background: #333333 !important;
    color: #ededed !important;
}
button,
input {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #646464), color-stop(1, #898989)) !important;
    border: 1px solid #FF8E34 !important;
}
.flot-overlay {
    display: none;
}
.chart * {
    background: none !important;
}
.form-popup-info,
.form-popup-info *,
div.section > h2 {
    background: #4C4C4C !important
}

.facts tr:nth-child(1) div.colour-key {
 	background: #edc240 !important;   
}


.facts tr:nth-child(2) div.colour-key {
 	background: #afd8f8 !important;   
}

.facts tr:nth-child(3) div.colour-key {
 	background: #cb4b4b !important;   
}

.facts tr:nth-child(4) div.colour-key {
 	background: #4da74d !important;   
}

.facts tr:nth-child(5) div.colour-key {
 	background: #9440ed !important;   
}
}
