css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiss-de.weedu.ch") {
    body{
    background:url(http://www.mastersong.de/templates/tk_engina_free/images/background.jpg) no-repeat;
		background-attachment:fixed;
		background-position:center center;
		background-color:#051008;    
    }
.box {
    display: inline-block;
    width: 100%;
    border: 1px solid #b3b3b3;
    padding: 0;
    margin: 3px;
    margin-bottom: 20px;
    width: 48%;
    float: left;
    border-radius: 10px;
    border: 2px solid green;
    background-color: white;   
}
    .box hgroup{
        border-radius: 10px;
    }
  box.full {
    width: 48%;
    float: left;
}
    .box hgroup div {
    float: left;
    width: 70%;
}

    .box hgroup div.actions{
    float: left;
    width: 30%;
}
    
    info, .info {
    background-color: white;
    display: inline-block;
    font-size: 0.5em;
    margin: 3px 0 ;
    padding: 5px;
    cursor: default;
    user-select: none;
    border: 1px solid #c10a27;
}
    navigation ul a{
        font-size: 2em;
        font-weight: bold;
        text-shadow: 1px 1px grey;
        padding: 20px 0 0 20px;
    }
    #mainGrid {
    background-color: transparent;
    padding: 10px;
    height: 100%;
    -moz-box-shadow: 1px 2px 17px 4px #b5b5b5;
    -webkit-box-shadow: 1px 2px 17px 4px #b5b5b5;
    box-shadow: 1px 2px 17px 4px #b5b5b5;
}

content.col-1-1{
  background-color: rgba(255,255,255,0.5);
}

    td img{
        height: 43px;
        width: 35px;
        float:right;
    }
    table.ng-scope{
        background-color:white;
        width:60%;
    }
    
    table[role="list"] td, table[role="list"] th {
    padding: 3px 6px;
    font-size: 13px;
    vertical-align: top;
    cursor: default;
}
    
    table[role="list"] thead th {
    font-weight: 600;
    text-align: left;
    font-size: 100%;
    padding: 0 5px;
}
    
    navigation .logo{
        background-color:white;
    }
    navigation.col-1-1{
        padding: 0 10px; 
        border-top: 4px solid #c10a27;
        background-color:white;
        margin-bottom: -10px;
    }
    
    header.col-1-1 {
       background-color:lime;
       padding: 0 10px; 
       margin-bottom: -5px;
        margin-top: -5px;
    }
    .textareaborder{
        height: 20px;
    }
    
    .breadcrumb{
        background-color:lime;
    }
}
