css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("monde-diplomatique.fr") {
  body { background: rgb(48, 48, 48) !important; color: white; }
  #page { background: rgb(0,0,0); }
  .notebox {background: #555; border-top: none; }
  .dates_auteurs .auteurs { color: #CFCBCB; }
  ul#menu li a { color: #fff; }
  .ariane a.filin, .ariane span, .ariane a.secondfil { color: #D2D2D2; }
  .voiraussi h2 span, .rappelsune .voiraussi h2 span { color: #A7A7A7; }
  .liste li .intro, .liste li a .intro { color: #C2C2C2; }
  .liste li a h3, .liste li a h4 { color: #ABABAB; }
  .bandeautitre { background: #a19f9f !important; }
}
