css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linustechtips.com") {

body, input,
#ipsLayout_body, .ipsPagination li a,
.ipsQuote, #elUserLink_menu
{
background-color:#000000 !important;
}
mark
{
background-color:#DB4105 !important;
}
.ipsDataItem_icon, .ipsDataItem_main, .ipsDataItem_stats,
.ipsDataItem_lastPoster, .ipsWidget, #elSearchFilters, span,
.ipsAreaBackground_light, .ipsStreamItem, .lmgComment_footer,
.lmgComment_header, .ipsComment_author, .cPost_contentWrap,
.ipsColumn, .ipsQuote p span, .advrep-profile,
#elProfileTabs_content, .ipsAreaBackground, #elProfileTabContent,
#ipsTabs_elSettingsTabs_setting_overview_panel
{
background-color:#111111 !important;
}
.ipsWidget_title, .ipsType_sectionTitle, .ipsButtonBar, .ipsQuote_citation, #elUserLink_menu li a:hover, 
.ipsType_pageTitle, .ipsType_normal
{
background-color:#333333 !important;
}
.ipsDataItem, .ipsQuote
{
border-color:#DB4105 !important;
}
.ipsComment_author
{
border-left:1px #DB4105 solid !important;
}
.lmgComment_header, .cPost_contentWrap, .lmgComment_footer
{
border-right:1px #DB4105 solid !important;
}
.ipsDataItem_icon, .ipsDataItem_main, .ipsDataItem_stats,
.ipsDataItem_lastPoster, .ipsPad,
.ipsComment_author, .lmgComment_header
{
border-top:1px #DB4105 solid !important;
}
.ipsType_sectionTitle + .ipsButtonBar,
.ipsComment_author, .lmgComment_footer,
.ipsBreadcrumb.ipsBreadcrumb_top
{
border-bottom:1px #DB4105 solid !important;
}
.ipsPagination li a, #elUserLink_menu
{
border:1px #DB4105 solid !important;
}
.ipsDataItem_meta p, .ipsDataItem_stats_number,
.ipsType_blendLinks, .ipsQuote, span
{
color:#D8CDCD !important;
}
h1, h2, h3, h4, h5, p, .ipsType_sectionTitle, input,
.ipsType_pageTitle, .ipsType_medium, .ipsBreadcrumb li,
.ipsResponsive_hidePhone span, .cAuthorPane_info li,
.ipsQuote_citation, .ipsDataItem_main, .ipsGrid li
{
color:#FFFFFF !important;
}
a
{
color:#FFFFFF !important;
text-decoration:underline !important;
}
a:hover
{
color:#DB4105 !important;
text-decoration:underline !important;
}
a span:hover
{
color:#DB4105 !important;
text-decoration:underline !important;
}
}
