@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("chrome://browser/content/aboutDialog.xul") {
  #clientBox {

	background: -moz-radial-gradient(85px 100px, ellipse farthest-corner,

rgba(130,137,142, .1) 35%, rgba(0, 0, 0,.5) 100%, rgba(0,0,8,0.1) 25%) !important;

	background-color: #82C6FD !important;

	font-size: 9pt !important;
color: whitesmoke !important;
}
  
  #leftBox {
    background-image: url(http://i.imgur.com/Ecer6Dv.png) !important;
    background-position: 50% 20%;
    background-repeat: no-repeat;
    min-width: 210px;
    min-height: 210px;
  }
  /*#rightBox {
    background-image: url(none) !important;
    background-size: auto 72px !important;
    margin-left: 30px !important;
  }*/
 #bottomBox {
   
   background: -moz-radial-gradient(100px 85px, ellipse farthest-corner,

rgba(130,137,142, .1) 35%, rgba(0, 0, 0,.5) 100%, rgba(0,0,8,0.1) 25%) !important;
background-color: whitesmoke !important;
    
font-size: 9pt !important;
}
}
