css:
@-moz-document domain("xkcd.com") {
body, #middleContainer {
		display: flex;
		flex-direction: column;
}
body > #middleContainer { order: -1; }
#middleContainer > #ctitle { order: -10 ;}
#middleContainer > #ctitle + .comicNav { order: -9 ;}
#middleContainer > #comic { order: -8 ;}
#middleContainer > #transcript { order: -7; }
}
