css:
@-moz-document domain("pajlada.se"), domain("forsen.tv"), domain("pajbot.com") {
body {
    background: #101010;
    color: #909090;
}
.ui.header {
    color: #b0b0b0;
}
.ui.top.fixed.menu {
    background: #1b1c1d;
}
.ui.menu .item {
    color: #909090!important;
}
.ui.menu.item:hover {
    background: #ccc!important;
    color: #000!important;
}
.ui.action.input {
    background: #3E3E3E!important;
    padding-left: 10px!important;
}
input,
i.black.icon {
    color: #a0a0a0!important;
}
.ui.action.input:not([class*="left action"])>.button:last-child,
.ui.action.input:not([class*="left action"])>.buttons:last-child>.button,
.ui.action.input:not([class*="left action"])>.dropdown:last-child {
    border-radius: 0;
}
::-webkit-input-placeholder {
    color: #767676!important;
}
.ui.button {
    background: linear-gradient(transparent, transparent) #1B1C1D;
    box-shadow: none;
    color: #909090!important;
}
.ui.twitch.button {
    color: #fff!important;
}
.ui.table {
    color: #7a7a7a;
    border: 1px solid #444;
}
.ui.table thead th {
    background: #181818;
}
.ui.table tbody {
    background: #080808;
}
.ui.very.basic.table tbody {
    background: inherit;
}
.ui.table td.active,
.ui.table tr.active {
    background: #282828!important;
    color: #fff !important;
}
.ui.selectable.table tbody tr:hover,
.ui.table tbody tr td.selectable:hover {
    color: rgba(255, 255, 255, .95)!important;
}
.ui.items>.item .meta {
    color: inherit;
}
.ui.table thead th,
.ui.items>.item>.content>.description,
.ui.list>.item .description {
    color: #777;
}
.ui.tabular.menu .active.item,
.ui.segment,
.ui.attached.segment {
    background: #2e2e2e;
    border: 1px solid #2e2e2e;
}
.ui.very.basic.table,
.ui.list>.item .header {
    color: #909090!important;
}
.ui.tabular.menu {
    border-bottom: none;
}
.ui.menu a.item:hover,
.ui.menu .active.item,
.ui.menu .active.item:hover,
.ui.vertical.menu .active.item:hover {
    background: #444;
}
.picker {
    color: inherit;
}
.picker__nav--next:before {
    border-left: 0.75em solid #999;
}
.picker__nav--prev:before {
    border-right: 0.75em solid #999;
}
.picker__day--disabled,
.picker__day--disabled:hover,
.picker--focused .picker__day--disabled {
    background: transparent;
}
.ui.styled.accordion,
.ui.styled.accordion .accordion {
    background: #080808;
}
.ui.styled.accordion .accordion .active.title,
.ui.styled.accordion .active.title {
    color: rgb(122, 122, 122);
}
}
