css:
@-moz-document domain(github.com) {
    .container {
        width: auto !important;
        margin: 10px !important;
    }

    .discussion-timeline {
        width: inherit !important;
        float: inherit !important;
        margin-right: 220px !important;
    }

    .timeline-new-comment {
        max-width: inherit !important;
    }

    #discussion_bucket .discussion-timeline .js-discussion .timeline-comment-wrapper .markdown-body:after {
        clear: none !important;
    }

    #discussion_bucket .discussion-timeline .pull-merging .merge-branch-form .commit-form .commit-form-actions:after {
        clear: none !important;
    }
}
