css:
@-moz-document domain(www.reddit.com) {
    /* safe for work dota 2 reddit */

    /* remove rightside bar */
    .side
    {
        display: none !important;
    }

    /* body of links full 100% */
    body .content
    {
        margin-right: 8px !important;
    }

    /* header black */
    #header,
    body.res-nightmode #header
    {
        background: #000 !important;
    }

    /* set opacity to 0 to remove dota 2 text (still clickable) (set to max 1 if you want it to be visible) */
    #header-bottom-left span
    {
        opacity: 0.2 !important;
    }

    /* set opacity to 0 to remove reddit logo (still clickable) (set to max 1 if you want it to be visible) */
    #header-bottom-left img
    {
        opacity: 0.1;
    }
  
    /* promotions gone. */
    .link.promotedlink
    {
        display: none !important;
    }
  
    /* newsletter gone. */
    .infobar.newsletterbar
    {
    	display: none !important;
    }

    /* black header links and other links so it looks like normal text. */
    a
    {
        color: #000 !important;
    }

    /* set opacity to 0 to remove dota 2 user flairs (set to max 1 if you want it to be visible) */
    .flair
    {
        opacity: 0.1 !important;
    }
  
}
