css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("classic.torrentleech.org") {

#torrents table tr td {
    font-size: 11px !important;
    height: 16px !important;
    overflow: hidden;
    white-space: nowrap;
  }

/* right ads banner */
  div[id="right"],
  iframe[id="Frame"] {
  display: none !important
  }
  
  #wrapper #main #middle {
    display: none;
  }

  #supportTorrentLeech { 
    display:none
  }

  #torrents tr td:nth-child(6) > br {
    display: none;
  }

  #torrents th:nth-child(9), #torrents tr td:nth-child(9) {
    display: none;
                    }
  
}
