css:
@-moz-document regexp(".*codestriker.*") {
TD.line, TD.file, TD.n, TD.a, TD.ab, TD.r, TD.rb, TD.c, TD.cb, TD.msc, pre, span.hl, pf, comments {
    font-family: monospace !important;
    font-size: medium !important;
    line-height: 150% !important;
}

a.nocom, a.comment, a.toc {
    font-family: sans-serif !important;
    font-size: small !important;
    line-height: 130% !important;
}

A.toc, TR.commentb, TR.commenth, TR.comments, TR.tlh, TR.tt1, TR.tl1, TR.tl2, select, a:link, a:visited {
    font-family: sans-serif !important;
    font-size: medium !important;
    line-height: 140% !important;
}

textarea, input {
    font-family: sans-serif !important;
    font-size: medium !important;
    line-height: 140% !important;
}

table {
    line-height: 100% !important;
}
}
