css:
@-moz-document domain("www.culture.gouv.fr") {
#conteneur::before {
    content: "Joconde — Service des Musées de France";
    font-size: 40px;
    font-weight: bold;
}
#conteneur #fondmenu,
#conteneur #entete,
#conteneur #pieddepage4,
#conteneur #partage {
    display: none;
}
#conteneur {
    height: auto;
    width: auto;
    left: 0;
    position: inherit;
    overflow: hidden;
    margin-left: 0;
}
#conteneur #centrepage {
    float: none;
    height: auto;
    width: auto;
    padding-left: 30px;
    padding-right: 30px;
    margin-top: 40px;
    text-align: justify;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
}
#conteneur #centrepage #pagecentre {
    float: none;
    height: auto;
    width: auto;
    padding-left: 30px;
    padding-right: 30px;
}
}
