css:
@-moz-document url-prefix("https://user.qzone.qq.com") {
/*元素屏蔽*/
.col-main-sidebar,.mod-side-nav-recently-used,#headContainer,#tab_hide_list,#tab_switch,#tb_vip_li,.feed-hot,.f-single-biz,#qz_notification,#js-qq-ad{display:none !important;}

  /*位置移动*/
.col-main-feed, .col-main-feed li{width:800px !important;}
.layout-nav{margin-top:150px !important;}
#main_feed_container{margin-top:-150px !important;}
.user-name{margin-top:15px;}
.head-avatar{margin-left:20px !important;}
.top-fix-inner{margin-top: 0px!important;}

  /*圆角头像*/
*[class*="user-avatar"],.q_namecard,.q_namecard img,.head-avatar{border-radius:100px !important;}

  /*背景更改*/
#layBackground,body{background: #EEE !important;}
.mod-side-nav .current .qz-grid, .mod-side-nav li:hover .qz-grid, .top-toggle:hover{background-color:#FFF !important;border: 1px solid #D9D9D9 !important;margin-bottom:-1px;}
.mod-side-nav{background-color: #EEE !important;border:0px !important;}
}
