css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("blog.iso50.com")
{
	img.header-anhr,
	img.iso50-header-img,
	#sidebar,
	.slashdigglicious {
		display: none !important;
	}

	#page {
		width: auto !important;
	}

	#page,
	#content {
		float: none !important;
	}

	#content {
		margin: 0 auto !important;
		background-image: url(http://iso50.com/sfca/images/70s_tile_repeater.gif);
	}

	#footer {
		width: 450px !important;
		background-image: url(http://iso50.com/sfca/images/70s_tile_repeater.gif);
	}

}
