css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spreeder.com") {
/* Change background color */
body { background-color:#2f2f2f !important; }

/* Remove text-box border */
div { border-width: 0 !important; }

/* Remove hassy elements */
div[id="footer"],
div[id="keycommands"],
div[id="header"],
div[id="stats"],
div[style="float: right; height: 600px; width: 120px;"]
{ display: none !important; }

/* Support the site!
iframe[width="120"]
{ display: none !important; }*/
}
