@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamepressure.com") {

html, head, body, .gpg-ban-top
{
background: #000000 !important;
}
.gp-top-res-c, .left, .right
{
background: #D23800 !important;
}
header h1, header h2, header p, .gc,
.img-podpis, .next-prev, .gpg-copyr,
article, .gpg-page, .gpg-page-right,
.gpg-top-back, .z2, .gpg-head
{
background: #222222 !important;
}
a, p, i, article,
h1, h2, h3, h4, h5, h6,
.img-podpis, .next-prev, .gpg-copyr
{
color: #FFFFFF !important;
}
a:hover, .z2, .toc-header
{
color: #FFB400 !important;
}
}
