css:
@-moz-document url-prefix("http://www.twitch.tv/") {
#large_nav .content, #small_nav .content {
    background-color: #222;
}

 #broadcast-meta, .info, .js-directory.tse-content {
    background: #111111;
}

a, .button, .js-sub-button, .over, .real {
	color: #417CD0;
}

.dropmenu.menu-like .dropmenu_action, .menu-like.ui-menu .dropmenu_action, .menu-like.ui-multiselect-menu .dropmenu_action, .dropmenu.menu-like .ui-menu-item a, .menu-like.ui-menu .ui-menu-item a, .menu-like.ui-multiselect-menu .ui-menu-item a, .dropmenu.menu-like .edit_action span, .menu-like.ui-menu .edit_action span, .menu-like.ui-multiselect-menu .edit_action span, .dropmenu.ui-menu .dropmenu_action, .ui-menu .dropmenu_action, .ui-menu.ui-multiselect-menu .dropmenu_action, .dropmenu.ui-menu .ui-menu-item a, .ui-menu .ui-menu-item a, .ui-menu.ui-multiselect-menu .ui-menu-item a, .dropmenu.ui-menu .edit_action span, .ui-menu .edit_action span, .ui-menu.ui-multiselect-menu .edit_action span {
	color: #417CD0;
}

.primary:hover {
	background: #58a6da !important;
}

.dropmenu_action:hover {
	color: #ffffff !important;
    background-color: #417CD0 !important;
}

.twitch_subwindow_container {
    background: rgba(65,124,208,.9);
}

.room-title {
	color: #417CD0 !important;
}

.room-title:hover {
	color: #ffffff !important;
}

.room:hover {
    background-color: #417CD0 !important;
}

.username, .header {
	color: #417CD0 !important;
    font-size: 120% !important;
}

.ember-follow.follow-button.is-following a {
	background-color: #417CD0 !important;
}

.button.glyph svg path, .follow-button:not(.ember-follow) .glyph.follow svg path, .button.glyph-only svg path, .follow-button:not(.ember-follow) .glyph-only.follow svg path {
    fill: #417CD0 !important;
}

.button.primary, .follow-button:not(.ember-follow) .follow {
	background: #417CD0;
}

.send-button {
	background: #417CD0 !important;
}

.masonry-brick {
	color: #BDC3C7 !important;
}

.ember-follow.follow-button:not(.is-following) a {
	background-color: #417CD0;
}

.button:hover, .send-button {
    fill: #2980B9 !important;
}

.undelete {
	color: #417CD0 !important;
}

#viewers a {
	color: #417CD0 !important;
}

.conversations-list-bottom-bar {
	background: none !important;
    border: none !important;
}

.links_group a, .warp__status {
	color: #2980B9 !important;
}

.warp__title {
	color: #417CD0 !important;
}

.ember-follow.follow-button:not(.is-following) a:hover {
	background-color: #58a6da !important;
}

#channel-prompt, .upsell-banner {
    margin: 30px 0;
    padding: 20px;
    background: #111111;
    position: relative;
    border-left: 5px solid #2980B9;
}

.upsell-banner p {
	color: #BDC3C7 !important;
}

.upsell-banner .title {
	color: #ECF0F1 !important;
}

.channel-stats .stat {
	color: #ECF0F1;
}

.channel-stats .live-count svg path {
    fill: #417CD0;
}

.channel-stats .stat svg path {
    fill: #417CD0;
}
}
