css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://global/content/alerts/alert.xul) {
  #alertNotification {
    background: transparent !important;
  }
  #alertBox[hasImage="true"] {
    background: whitesmoke !important;
  }
  #alertBox[hasImage="true"] #alertImageBox {
    background: whitesmoke !important;
  }
  #alertBox[hasImage="true"] #alertImageBox #alertImage[src] {
    max-height: 40px !important;
    max-width: 40px !important;
    margin: 5px !important;
    margin-top: -20px !important;
  }
  #alertTitleBox {
    margin: 0px !important;
    background: -moz-dialog !important;
  }
  #alertBox[hasImage="true"] #alertTitleBox {
    margin: 0px !important;
    margin-left: 50px !important;
  }
  #alertNotification[style="direction: rtl;"] #alertBox[hasImage="true"] #alertTitleBox {
    margin: 0px !important;
    margin-right: 50px !important;
  }
  #alertBox[hasImage="true"] #alertTitleLabel {
    margin-left: 5px !important;
    margin-right: 5px !important;
  }
  #alertBox[hasImage="true"] #alertTextBox {
    margin: 0px !important;
    margin-bottom: 3px !important;
  }
  #alertBox:not([hasImage="true"]) #alertTextBox {
    margin-bottom: 10px !important;
  }
  #alertBox[hasImage="true"] #alertTextBox #alertTextLabel {
    margin: 0px !important;
    margin-left: 15px !important;
  }
  #alertNotification[style="direction: rtl;"] #alertBox[hasImage="true"] #alertTextBox #alertTextLabel {
    margin: 0px !important;
    margin-right: 13px !important;
  }
  #alertBox box {
    background: -moz-dialog !important;
  }
  #alertFooter {
    display: none !important;
  }
}
