@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bazos.sk") {
  
  @font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
  
html{
  background: #e8e8e8;
  padding-top: 25px !important;
  padding-left: 25px !important;
  padding-right: 25px !important;
  padding-bottom: 25px !important;
  
  }
  
  img[alt="Bazos.sk - Inzercia, inzeráty"]:hover {
    -webkit-filter: grayscale(0%) !important;
    -webkit-transition: .5s ease-in-out !important;
    filter: grayscale(0%) !important;
    transition: .64s ease-in-out !important;
    -o-filter: grayscale(0%) !important;
    -o-transition: .5s ease-in-out !important;
  } 

  img[alt="Bazos.sk - Inzercia, inzeráty"] {
    -webkit-filter: grayscale(100%) !important;
    -webkit-transition: .5s ease-in-out !important;
    filter: grayscale(100%) !important; 
    transition: .32s ease-in-out !important;
    -o-filter: grayscale(100%) !important; 
    -o-transition: .5s ease-in-out !important;
  }

  .inzeraty , .galleryobal{
    transition: .16s ease-in-out;
  }

  body{
    box-shadow: 0 6px 12px 0 #8e8e8e !important;
    border-radius: 3px !important;
  }

  body, td, div, .p, a{
    font-family: 'Open Sans',Arial,Helvetica, sans-serif !important;
  }
  
  .barevne{
    background-color: RGBA(255, 255, 255, 0) !important;
    border: 0px solid RGBA(255, 255, 255, 0) !important;
    min-height: fit-content !important; 
  }
  
  .gallerylist{
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(data:image/gif;base64,) no-repeat;
  width: 18px; /* Width of new image */
  height: 16px; /* Height of new image */
  padding-left: 18px; /* Equal to width of new image */
  border: 0px solid;
  }
  
  
  .gallerylista{
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(data:image/gif;base64,) no-repeat;
  width: 18px; /* Width of new image */
  height: 16px; /* Height of new image */
  padding-left: 18px; /* Equal to width of new image */
  border: 0px solid;
}
  
  #form2{
    margin-left: 5px;
  }
  
  iframe{
  min-height: 350px !important;
  }

  
  .sirka{
    margin: auto !important;
    width: 72% !important;
    padding: 10px !important;
  }
  
  div , .listainzerat , .cisla , .listah , .inzeraty{
    border-radius: 3px !important;
  }
  
  .listal > * {
    border-radius: 0px !important;
  }
  
  .listah , .nadpismenu{
    background-color: #848484 !important;
    border: 1px solid #2A2A2A !important;
  }
  
  #zvyraznenikat{
    COLOR: #000 !important;
  }
  
  .bodyr{
    background-color: #FFFFFF;
  }
  
  .cisla, .barvalmenu , .listainzerat , .plupload_container , .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default , .ui-widget-header, .pridati{
    background-color: #DEDEDE !important;
    border: 1px solid #9E9C9B !important;
  }

  .inzeraty:hover , .galleryobal:hover , .act:hover{
    background-color: #DEDEDE !important;
  }
  
  A:hover{
    text-decoration: unset !important;
    color: #7B7B7B !important;
  }

}
