css:
@-moz-document domain("arstechnica.com") {
	.home .column-1, .column-2 {
		float: none;
		width: auto;
	}
	.home article {
		clear: both;
		overflow: hidden;
	}
	.home figure.overlay {
		float: right;
		max-height: 110px;
		overflow: hidden;
	}
	.home #stories-main h1 {
		margin-top: 0;
	}
	.home .main-feature-alt {
		background-size: 300px;
		background-position: right top;
	}
	.home .main-feature-alt .feature-contents {
		margin-top: 0;
		margin-right: 300px;
	}
	.home #radar{
		float: none;
		width: auto;
	}
	.home #stories-features img {
		float: right;
	}
	.home article .comments-hotness {
		bottom: 7px;
	}
}
