css:
@-moz-document url-prefix("http://www.codeabbey.com") {
/* 
 * Name:    Codeabbey Darkgray
 * Author:  Crystal_HMR
 * Version: 0.02 {20160223}
 * Comment: first step to gray codeabbey.com
 */

body {
    background-color: #414141;
    color: #ebebeb;
}

a {
	color: #A388F4;
}

.navbar a{
    color: #A388F4;
}

pre {
	background-color: #232c31;
}

code, pre code {
    background-color: #2e2e2e;
    color: #8080eb;
}

.form-control {
	background-color: #505050;
    color: #ebebeb;
}

.ace-tm {
    background-color: #2e2e2e;
    color: #ebebeb;
}

.ace-tm .ace_gutter {
    background: #232c31;
    color: #8080eb;
}

button {
    background-color: #232C31;
}

.btn-default {
    background-image: linear-gradient(to bottom, #414141, #A2A2A2 100%);
}

.header, .footer {
    background: #747981;
    border-top: 1px solid #76768B;
    border-bottom: 1px solid #76768B;
}

button, html input[type=button] {
    background:#515151;
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #5F5F5F;
}
.table-striped>tbody>tr:hover>td {
    background: #ffa;
}

.solved {
    background-color: #7FAD7F !important;
    color: #5F5FAF;
}
.full-width {
    background-color: grey;
}
}
