css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hitek.fr") {
  
  .header-basics{
    background-color: #A8A8A8 !important;
    border-bottom: 1px solid black;
  }
  
  
  /* Lien couleur rose hover */
  .dropdown-menu a:hover, .load-articles.pointer:hover, .popular-link a:hover, td h3 a:hover{
    text-decoration: underline !important;
    color: #FFADB2 !important;
  }
  
  .dropped-menu.revealtouch, .dropped-container, .header-container2, .content-container, .footer-container, .header-menu, .articles-like.sticker-like, .quiz, .content-footer, .forum-hot-topics, .follow, .products-preview{
    background-color: #494949 !important;
  }
  
  body{
    background-image: none !important;
    background-color: black !important;
  }
  
  .content-left, .content-basics{
    width: 100% !important;
  }
  
  .habillage, .content-right, .hitv{
    display: none !important;
  }
  
  
  .article p, .news-content p, .dropped-menu a, .dropdown-menu a, .forum-hot-topics p a, .article, .content-comments-text{
    color: white !important;
  }
  
  
  .article h2, .article a, .quiz h3, .count.red.block, .hot-title, .hot-img a, .content-title h2, .news-content a, .popular-title, .popular-link a, td h3 a{
    color: #FFADB2 !important;
  }
  
  .article a:hover, .forum-hot-topics p a:hover{
    text-decoration: underline !important;
  }
  
  blockquote em, blockquote span, .answer, .scoring-question{
    color: black !important;
  }
  
  blockquote{
    background: none !important;
  }
}
