css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wahackforo.com") {
  body {
    background: white url(http://i.imgur.com/ekxoLHI.png) right bottom fixed no-repeat;
    color:#333;
  }
  
  #forum_container {
    width: 940px;
    margin-top:20px;
    border: 1px solid #ccc;
    text-align:left;
    line-height:1.4;
    color:#333;
    border-radius: 3px !important;
    font-style:normal;
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.3) !important;
}
  #header {
    background: url(http://i.imgur.com/Lhzta33.png);
    border-radius: 0;
  }
  
  .post-entry img {
    max-width: 100% !important;
  }
  
  .alt1 {
    color: black;
  }
  .lower_panel {
    background: #7e6a57;
        color: #eee;
  }
   .lower_panel a {
    color: #eee !important;
  } 
  .upper_panel a {
    color: white !important;
  }
  
  .tcat {
    background: #74a19b;
    border-radius: 0;
  }
  
    .tborder, forum_tborder {
    border-radius: 0 !important;
  }
  .upper_panel {
   text-shadow: none;
    font-weight: normal;
  }
  .upper_panel strong {
    font-weight: normal;
  }
  .lower_panel a.logout {
    color: #3B3B3B;
  }
  .upper_panel a {
    color: #3B3B3B;
    font-weight: normal;
  }
  
  #infoarea-foro {
    z-index: 9999;
    width: 940px !important;
    height: 165px !important;
    margin-top: -160px;
    margin-left: -5px;
  }
  
  .alt2 img {
    height: auto;
    width: auto;
  }
  
  .signature {
    border-top: 1px dashed #ccc;
    background: #fff;
    max-height: 250px;
    overflow-x: hidden;
    clear: both;
    overflow-y: auto;
    margin-top: 5px;
    padding: 4px;
    table-layout: fixed;
}

  .vbseo_links {
    visibility: visible !important;
  }
  .alt1, .alt1Active, .alt2, .alt2Active { background: #F5F5F5; color: #222; } 
}
