css:
a[href^="http:"]:hover {
  COLOR: ORANGE;
}
a[href^="https:"]:hover {
  COLOR: GREEN;
}
a[href*=".exe"]:hover {
  COLOR: RED;
}
a[href*=".msi"]:hover {
  COLOR: YELLOW;
}
a[href*=".app"]:hover {
  COLOR: RED;
}
a[href*=".deb"]:hover {
  COLOR: PURPLE;
}
a[href*=".dmg"]:hover {
  COLOR: PURPLE;
}
