css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myepisodes.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, menuText, divMenu, .menuText, .divMenu

{
color: white !important;
background: rgb(32,32,32) !important;
}


#divContainer
{
	width: 850px;
	/*position: relative;*/
	padding: 2px;
	border-left: solid 1px #CCCCCC;
	border-right: solid 1px #CCCCCC;
	background-color: rgb(32,32,32);
	/* Positioning container to center in (IE) */
	/*left: 50%;*/
	/*margin-left: -380px;*/
}
  
  
table, a
{
  color: #6D9FB2 !important;
}
  
logo
  {
    display: none !important;
  }  
  
#divAboveMenu
{
	background: rgb(32,32,32) !important;
	position: relative;
  
}

#divMenu
  {
    background: #4a4a4a !important;
  }
  
#acGlobalSearch
  {
    left: 300px !important;
  }
  
#menuText
{
	clear: left;
	margin-left: 2px;
	padding: 3px 5px 5px 5px;
	background: rgb(32,32,32) !important;
}

#menuText a:hover
{
	color: coral;
	text-decoration: none;
}  


/* Mainmenu list item */
#navlist .mmli
{
	display: none !important;
}

/* Mainmenu list item */
#navlist .mmlilast
{
	display: none !important;
}

/* All links in the menus */
#navlist a
{
	color: #054f5c;
	text-decoration: none;
}

/* All hovered links in the menus */
#navlist a:hover
{
	color: #01171b;
}

#myepisodes_views div.private_list 
{
	display: none !important;
}  
  
#divMenu
{
	border: solid 1px rgb(32,32,32) !important;
	width: 100%;
	clear: right;
}
 

#rsshelp div.pageheader
{
	display: none !important;
}
#rsshelp div.pageheader div 
{
	display: none !important;
}  
  

}
