css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rpnow.net") {
body {
    background-color: #333;
    color: #ccc;
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 12px;
}
h1 { color: #00b386; font-family: 'Trocchi', serif; font-size: 45px; font-weight: normal; line-height: 48px; margin: 0; }
h2 { color: #00b386; font-family: 'Trocchi', serif; font-size: 45px; font-weight: normal; line-height: 48px; margin: 0; }

a:link {
        text-decoration: none;
        color : #00b386;
}
a:visited{text-decoration: none;
        color : #00b386;}
a:hover {
        text-decoration: none;
        color : #1affc6;
    }
a:active{
        text-decoration: none;
        color : #ffffff;
    }
    
    
/*______ Home ______*/
    input[type="text"], textarea {
    background-color: #333;
    border: solid 1px #333;
    box-shadow: 0 1px 1px #000 inset;
    font-size: 12px;
    color: #ccc;
}
    form.form-box input[type="submit"] {
    font-size: 12px;
    margin-top: 10px;
    background-color: #333;
    color: #ccc;
    border: solid 1px #555;
    font-weight: bold;
}
    input[type="submit"]:not(.btn-margin), button:not(.btn-margin), .btn-margin > span {
    background-color: #333;
    border: solid 1px #555;
    box-shadow: 0 1px 2px #000;
     color:#ccc;
    padding: 6px 12px;
    border-radius: 8px;
}
   
/*______ RP ______*/
header {
    border-bottom: ridge;
    margin-bottom: 20px;
    font-size: 15px;
}
header a{
    font-size: 12px;
}

#messages {
    background-color: #333;
    color:#ccc;
    text-align: right;
}
#messages > .message-narrator {
    background-color: #333;
    box-shadow: 0 0px 0px #333;
}
#messages > .message-chara > .content {
    background-color: #333;
    color:#ccc;
}

#messages > .info {
    font-size: 16px;
    text-align: center;
    background-color: #333;
    margin: 20px;
    padding: 5px 10px;
    border: 3px ridge #555;
    
}
.message-box-chara #message-voice {
    box-shadow: 0px 1px 4px #000;
}
    
    #messages > .message-chara > .content span.action {
    opacity: 0.7;
}

}
