css:
@-moz-document domain(youtube.com) {  
  .watch-stage-mode .player-height {
    height: 100vh !important;
  }
  .watch-stage-mode .player-width {
    left:0% !important;
    width: 100% !important;
    padding-right:0px;
  }
  .watch-stage-mode .player-width {
    margin-left: 0% !important;
  }    
  element.style {
    left:0% !important;
    width: 100% !important;
    height: 56.25vh  !important;
  }  
.watch-stage-mode video {
    left:0% !important;
    top:0% !important;;
    width: 100vw !important;
    height: 100vh !important;
  }
  .watch-stage-mode #player, .watch-stage-mode #placeholder-player {
  margin-top: -60px !important;
  }
  #yt-masthead-container {
    opacity: 0 !important;
    transition: opacity .1s .1s !important;
  }
  #yt-masthead-container:hover {
    opacity: 1 !important;
  }  
  .watch-stage-mode .player-width, .ytp-chrome-bottom, #watch7-container {
  left: 0;
  right: 0;
  margin: 0 auto;
  }
}
