css:
@-moz-document domain("disconnect.me") {
body {
        background: #131313;
}
.hdtb_mitem a {
         border: 0px solid #fafafa;
}
header {
    background: #131313;
}
div#results #normal-results li, #sidebar_txt li {
    color: #767676;
}
#normal-results a {
    color: #979797!important;
}
input:focus, select:focus, textarea:focus {
    background-color: #767676;
    border: 0px solid #767676;
    border-color: #979797;
    outline: 0;
    -moz-box-shadow: 0 0 0em #767676;
    -webkit-box-shadow: 0 0 0em #767676;
    box-shadow: 0 0 0em #767676;
}
input, select, textarea {
    background-color: #767676;
	border: 0px solid #979797;
}
.top-form-input.home {
    border: 1px solid #000; 
}
#main-search input[type=text] {
    background: #767676;
}
#normal-results a:visited {
    color: #979797!important;
}
#tabs {
    border-bottom: 0px solid #dfdfdf; 

}
element.style {
    border-bottom: 0px solid #000;
}
div#results #normal-results.image-results {
    background: #131313;

}
body a, #normal-results a {
    color: #767676;
}
.pagination .text-link {
    color: #767676;
}
}
