css:
@-moz-document domain("luminous.elcenia.com") {
html {
    width: 100%;
    background-color: #000;
}

body {
    color: #ddd !important;
    font-family: "Open Sans", sans-serif;
    max-width: 700px;
    margin: auto;
}

img[src='/images/banner.png'] {
    display: none;
}

a[href='/story.shtml'] img {
    display: none;
}

a[href='/links.shtml'] img {
    display: none;
}

a[href='/about.shtml'] img {
    display: none;
}

a[href='/rss.xml'] img {
    display: none;
}

map[name='color'], img[usemap='#color'] {
    display: none;
}

p {
    text-indent: 0pt;
}

a {
    text-decoration: none;
}

td[valign='top'] div[align='center'] a {
    padding: 10px;
}

td[valign='top'] div[align='center'] a:before {
    font-weight: bold;
    font-size: 16pt;
}

td[valign='top'] div[align='center'] a img {
    display: none;
}

td[valign='top'] div[align='center'] a:nth-last-child(4):before {
    content: "First";
}

td[valign='top'] div[align='center'] a:nth-last-child(3):before {
    content: "Prev";
}

td[valign='top'] div[align='center'] a:nth-last-child(2):before {
    content: "Next";
}

td[valign='top'] div[align='center'] a:nth-last-child(1):before {
    content: "Last";
}

div#foot {
    height: 500px;
}
}
