css:
@-moz-document domain("twitter.com") {
*[dir=rtl], div[dir=rtl], span[dir=rtl],
 .tweet-text-rtl {font-family: "Droid Arabic Naskh", sans-serif !important; font-size: 125%; line-height: 145%;}
.TweetTextSize {font-weight: bold !important; font-size: 18px !important; line-height: 26px !important;}
.TwitterCardsGrid, .AdaptiveSearchTitle, .SignupCallOut, .media-not-displayed {display: none !Important;}
.AppContainer, .AdaptiveSearchPage, .AppContent-main  {max-width: 1000px !important; min-width: 999px !important;}
.u-size1of3  {width: 24.3333% !important;}
.u-size2of3  {width: 75.6667% !important;}
.content-main {width: 750px !important;}
.mejs-shim {max-width: 650px !important; min-height: 320px !important; max-height: 550px !important;}
.AdaptiveMedia-photoContainer  {max-width: 650px !important; max-height: 550px !important; min-height: 350px !important;}
.content, .hidden {display:inherit !important;}
}
