css:
@-moz-document domain("zeldauniverse.net") {


.wbbThreadList .table td.columnSubject h3 a {
    font-weight: 400!important;
}

.messageGroupList tr.new .columnSubject > h3 > .messageGroupLink {
    font-weight: bold!important;
}

.quoteBox.containerPadding {
    padding-left: 54px;
    background: rgba(202, 157, 75, 0.23);
    border-radius: 15px;}

.quoteBox > header {
    padding-bottom: 4px;
    border-bottom: 1px dotted #D6B159;
    margin-bottom: 7px;
}

.messageHeader + .messageBody > div:first-child, .messageBody > div:not(.messageFooter):not(:first-child) {
    border-top: 1px solid rgba(216, 173, 47, 0.12);}

.message .messageHeader .permalink {
    color: #9A7563;
}
}
