css:
@-moz-document url-prefix("https://cronometer.com/") {
#cronometerApp {
  width: 100%;
  margin: 0;
  max-width: none;
}

.GL-TVABCGT > table {
  width: 100%;
  margin: 0;
}

.GL-TVABCGT > table > tbody > tr > td:nth-child(1) {
  width: 150px;
}

/* The container */
.GL-TVABCHT {
   width: auto;
   position: relative;
}


.GL-TVABCHT > * {
  width: 49%;
  box-sizing: border-box;
}

/* The nutrition panel */
.GL-TVABCNFB {
  position: absolute;
  top: 0;
  right: 0;
}
}
