css:
@-moz-document domain("gamefaqs.com") {
 .ffaq pre.faqtext {
    display: table;
    font: 14px "Consolas", "Consolas", monospace !important;
    margin: 0 auto !important;
    }
    @media (max-width: 767px) {
      .ffaq pre.faqtext {
        font: 11px "Consolas", "Consolas", monospace !important; } }
    @media (max-width: 480px) {
      .ffaq pre.faqtext {
        font: 7px "Consolas", "Consolas", monospace !important; } }
    @media (max-width: 767px) {
      .ffaq pre.faqtext {
        overflow-x: auto; } }
    @media (min-width: 768px) {
      .ffaq pre.faqtext {
        padding: 25px 100px; } }
    @media (max-width: 767px) {
      .ffaq pre.faqtext {
        background: none !important; } }
  .ffaq.imgmain {
    display: table;
    margin: 0 auto; }
    .ffaq.imgmain p {
      font-size: 12px;
      margin-bottom: 0; }
    .ffaq.imgmain img.imgresize {
      max-width: 100%;
      width: 100%; }
}
