css:
@-moz-document url-prefix("http://www.byfandom.org/") {
body,
.ow_canvas,
.jhtmlarea.ow_smallmargin,
.ow_bg_color,
.ow_box.ow_stdmargin.clearfix,
.ow_box_cap,
.ow_box.ow_stdmargin.clearfix.profile-BLOGS_CMP_UserBlogWidget,
.ow_box.ow_stdmargin.ow_break_word
{
/* This colors the background of the page */
    background-color: #dfecec !important;
}

.ow_header_pic {
/*this edits header image*/
    background-image: none !important;
    height: 0px !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #1d2f2f;
}

.ow_table_3 td.ow_value {
/* This affects tables*/
    width: 100%;
    padding-left: 0px;
    font-size: 100%;
    box-sizing: content-box;
    word-wrap: break-word !important;
}
.ow_canvas {
/*sets width of main boxes*/
    width: 1100px !important;
}
 
.ow_remark{
  color: black !important;
}
  
.ow_site_panel.clearfix{
/*This colors the top menu bar*/
  background-color: #eff5f5 !important;
}
  
body,
html {
/* This colors the color and size of regular text site-wide */
    color: black !important;
    font-size: 16px !important;
    line-height: 18px !important;
}

a {
/* This colors the color of link text site-wide */
    color: #660033 !important;
    text-decoration: none;
}


.ow_newsfeed_body,
.ow_dnd_widget.group-FORUM_CMP_LatestTopicsWidget,
.ow_alt2, .ow_alt1,
.feed_item_sharing_section.ow_box,
.ow_newsfeed_comments,
td,
th
{
/* This colors the the background of most container boxes on the newsfeed, blogs, and forums */
    background-color: #dfecec !important;
}

.ow_tr_delimiter td {
/* This prevents the background color of the forum boxes from over running their container */
    background-color: transparent !important;
}

.ow_tooltip_body, .ow_tooltip {
/* This colors the activity notification boxes at the top of the site */
    background-color: #eff5f5 !important;
}

}
