css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("espn.go.com") {
#global-scoreboard {
  display: none;
}

header {
  display: none;
}

#pane-main {
  padding-top: 0 !important;
}

.ad-slot {
  display: none;
}
.iframe-video {
  display: none;
}

#news-feed {
  display: none;
}

#article-feed {
  width: 100%;
}

#article-feed .article .article-footer, #article-feed .article .article-header, #article-feed .article .article-body {
    margin: 0 auto;
    max-width: none;
}
  
  .article-body {
    padding-left: 0 !important;
  }
  
  .article-social {
    display: none;
  }
}
