css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.theverge.com") {
  .m-ad, .m-nav__container, .p-entry-nav, .m-linkset, .yahoo-recommends, .m-footer {
    display: none !important;
  }
  .m-entry__body .m-entry__sidebar {
    display: none !important;
    vertical-align: top;
}
  .m-entry, .m-comments {
    width: 800px;
    margin: 0 auto;
  }
}
