css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("avito.ru"){

.category-map ,.catalog-promo, .context-flyer, #ads_direct_low, #ads_ldr_low, .item-highlight  {opacity:0.2; transition: opacity .4s ease-in-out;}
  .category-map:hover, .catalog-promo:hover, .context-flyer:hover ,#ads_direct_low:hover, #ads_ldr_low:hover, .item-highlight:hover {opacity:1; transition: opacity .3s ease-in-out;}
  
  .main-slider, .sendout, .adFox-1000-120, .js-avito-10-years-banner {display:none;}
  .c-b-2, .c-b-payed {background:#fff;}

  .item_table.item-highlight .item-description-title-link {background-color:inherit;}
 
  .js-catalog-vips-container {transform: matrix(1,0,0,1,0,100); opacity:.05; transition:.5s all ease;}
  .js-catalog-vips-container:hover {transform: matrix(1,0,0,1,0,0); opacity:1; transition:.3s all ease;}

.avito-ads-container {transform: matrix(1,0,0,1,0,1); opacity:.05; transition:.5s all ease;  min-height:12px;}
.avito-ads-container:hover {transform: matrix(1,0,0,1,0,0); opacity:1; transition:.3s all ease; min-height:120px;}

  
  .item .catalog-delivery  {background:#ffa6a6;}
}
