css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(dict.leo.org) {
#mainContent >  .fl-left {
    visibility: hidden;
    display: none;
  }
  
  html {
    background-color: #222;
    color: white;
  }
  
  .bg-lightyellow {
    color: unset;  
  }
  
  
.section > footer.more {
    background: rgb(0, 0, 0);
  }
  
  .bg-darkyellow {
    background-color: black;
    color: white;
  }
  
  .bg-darkblue {
    background-color: black;
  }
  
  .dropdown > span {
    color: white;
  }
  
  #switchSearchMode{
    color: white;
  }
  
  .tblf-alternate > tbody > tr:nth-child(2n) {
    background-color: #333;
  }
  
  .search {
    background-color: #222;
  }
  
  .isRelinked, .isRelinked b, .isRelinked b a {
    color: #aaa;
  }
  
  .button-raised {
    background-color: black;
  }
  
  .section.forumResults table tbody tr:nth-child(4n+2) td, 
  .section.forumResults table tbody tr:nth-child(4n+1) td {
    background-color: #222;
}
  
  .section.forumResults table tbody tr:nth-child(4n+3) td, 
  .section.forumResults table tbody tr:nth-child(4n+4) td {
    background-color: #333;
}
  
  .search input[type="text"], .search input[type="search"], #search-field:active, #search-field:focus {
    background-color: #222;
    color: white;
  }
  
  .tblf-alternate > tbody > tr {
    background-color: #222;
  }
  
  #centerColumn {
    margin-left: 100px !important;
    margin-right: 100px !important;
  }
  
  #searchBar {
    background-color: black;

  }
  
  #right {
    display: none !important;
  }
  #mainContent {
    width: 100%;
  }
  
  body {
    max-width: unset !important;
  }
  
  .banner-big {
    display: none;
  }
  
   #topBranding {
    padding-bottom: 2px !important;
     padding-top: 2px !important;
     height: unset !important;
  }
  
  #topBranding > .fl-left {
    margin-top: 0;
  }
  
  a:link, a:visited, a {
    color: #aaa;
  }
}
