css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filehippo.com") {
  #soft-news,
  .social-buttons,
  .featured-software,
  #ad-slot-2-large,
  #ad-slot-3-large,
  .ad-unit-label,
  #techbeat-widget,
  #header-above-content-leaderboard,
  .ad-unit-label + div,
  .outbrain-container,
  .footer-space {
    display: none;
  }
  
  #categories-list li a,
  #popular-list li a {
     width: 260px;
  } 
  
  .home-main-left {
    width: 100%;
  }
  
  .left-hand-col {
    width: 100%;
    margin-left: 0;
    padding-left: 30px;
    box-sizing: border-box;
  }
  
  .home-cat-container {
    width: 50%;
  }
  
  #popular-list li a {
    width: 100%;
  }
  
  .home-right-col {
    display: none;
  }
  
  #program-description-video {
    display: none;
  }
  
  .download-page-col1-content {
    display: none;
  }
  
  /* download confirmation screen */
  #ob_holder,
  #outbrain_widget_0 {
    display: none;
  }
  
  /* hide recommended apps title */
  h2[style="padding-top: 15px"] {
    display: none;
  }
  
  /* hide recommended apps */
  iframe[width="728"] {
    display: none;
  }
  
}
