css:
@-moz-document domain("bochs.sourceforge.net") {
body > table > tbody > tr:nth-child(4) > td:nth-child(2) > table,
body > div.SECTION,
body > div.CHAPTER,
body > div.BOOK
{
    max-width: 40em;
    padding: 0 0.5em 0 0.5em;
    margin: 0 auto 0 auto;
    -webkit-box-shadow: 1px 1px 3px 0px rgba(150,150,150,1);
    -moz-box-shadow: 1px 1px 3px 0px rgba(150,150,150,1);
    box-shadow: 1px 1px 3px 0px rgba(150,150,150,1);
}

body > table > tbody > tr:nth-child(4) > td:nth-child(2) > table
{
    margin: 1em auto 1em auto;
}

body > div.SECTION,
body > div.CHAPTER,
body > div.BOOK
{
    padding: 1em 2em 1em 2em;
}

body > table > tbody > tr:nth-child(4) > td:nth-child(2) > table > tbody > tr > td *
{
    max-width: 37em;
}
    
}
